// props통해 매개변수 전달 받아옴
const DiaryItem = ({ author, content, emotion, created_date, id }) => {
    return <div className="DiaryItem">
        <div class="info">
            <span>작성자: {author} | 감정점수: {emotion}</span>
            <span class="date">{new Date(created_date).toLocaleString()}</span>
        </div>
        <div>{content}</div>
    </div>

}

export default DiaryItem;

